import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Employee } from '../models/employee';
@Injectable({
  providedIn: 'root',
})
export class AuthService {
  constructor(private http: HttpClient) {}
  baseServerUrl = 'https://localhost:7149/api/';
  private apiUrl = 'https://localhost:7149/api/Employee';
  updateEmployee(employeeModel: Employee): Observable<any[]> {
    return this.http.put<any[]>(
      this.apiUrl + '/update-Employee-by-id',
      employeeModel
    );
  } 
  signinUser(signininfo: Array<string>) {
    return this.http.post(
      this.baseServerUrl + 'Auth/Register',
      {
        UserName: signininfo[0],
        PassWord: signininfo[1],
      },
      {
        responseType: 'text',
      }
    );
  }

  getEmployeebyid(id: string): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiUrl}/Get-Employee-by-id/${id}`);
  }
  signinUser(signininfo: Array<string>) {
    return this.http.post(
      this.baseServerUrl + 'Auth/Register',
      {
        UserName: signininfo[0],
        PassWord: signininfo[1],
      },
      {
        responseType: 'text',
      }
    );
  }
  loginUser(logininfo: Array<string>) {
    return this.http.post(
      this.baseServerUrl + 'Auth/Login',
      {
        UserName: logininfo[0],
        PassWord: logininfo[1],
      },
      {
        responseType: 'text',
      }
    );
  }
  IsloggedIn() {
    return !!localStorage.getItem('token');
  }
  IsloggedOut() {
    console.log('AuthService - logout');
    localStorage.removeItem('token');
  }
}
