import { Component, OnInit } from '@angular/core';
import {
  FormGroup,
  FormBuilder,
  FormArray,
  FormControl,
  Validators,
} from '@angular/forms';
import { AuthService } from 'src/app/services/auth.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Employee } from 'src/app/models/employee';
import { createEmployee } from 'src/app/models/createEmployee';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css'],
})
export class AddComponent implements OnInit {
  constructor(
    private signinAuth: AuthService,
    private router: Router,
    private Router: ActivatedRoute
  ) {}
  departments: any[] = [];
  ngOnInit(): void {
    this.signinAuth.getAllDepartments().subscribe((data: any[]) => {
      this.departments = data;
    });
}
  addEmployee = new FormGroup({
   // id: new FormControl('', [Validators.required]),
    name: new FormControl('', [
      Validators.required,
      Validators.minLength(3),
      Validators.maxLength(14),
    ]),
    depid: new FormControl('', [Validators.required]),
    cnic: new FormControl('', [
      Validators.required,
      Validators.minLength(13),
      Validators.maxLength(13),
    ]),
    age: new FormControl('', [
      Validators.required,
      Validators.minLength(2),
      Validators.maxLength(2),
    ]),
    salary: new FormControl('', [
      Validators.required,
      Validators.minLength(5),
      Validators.maxLength(6),
    ]),
  });
  //isUserValid: boolean = false;
  Submit(): void {
    const employee: createEmployee = {
    //  id: parseInt(this.addEmployee.get('id')?.value || ''),
      name: this.addEmployee.get('name')?.value,
      age: parseInt(this.addEmployee.get('age')?.value || ''),
      salary: parseInt(this.addEmployee.get('salary')?.value || ''),
      cnic: this.addEmployee.get('cnic')?.value,
      depId: parseInt(this.addEmployee.get('depid')?.value || ''),
    };
    this.signinAuth.addEmployee(employee).subscribe(resp => {
      if (resp.length === 0) {

        alert('Unsuccessful');
      } else {
        alert('Successfuly Added');
        this.router.navigate(['pages/home']);
      }
    });
  }
 // get id(): FormControl {
   // return this.addEmployee.get('id') as FormControl;
  //}
  get name(): FormControl {
    return this.addEmployee.get('name') as FormControl;
  }
  get depid(): FormControl {
    return this.addEmployee.get('depid') as FormControl;
  }
  get cnic(): FormControl {
    return this.addEmployee.get('cnic') as FormControl;
  }
  get age(): FormControl {
    return this.addEmployee.get('age') as FormControl;
  }
  get salary(): FormControl {
    return this.addEmployee.get('salary') as FormControl;
  }
}

<div class="container pb-1 ">
    <div class="mx-auto border p-4 bg-light " style="width: 400px; ">
  <h2 class="text-center" >Create</h2>
  <form [formGroup]="addEmployee" (ngSubmit)="Submit()">
      <div class="mb-3">
        <label class="form-label" for="name">Name:</label>
        <input type="text" class="form-control" id="name" placeholder="Enter your Name" formControlName="name"  required>
        <div class="form-text text-danger"  *ngIf="name.errors?.['required'] && (name.dirty || name.touched)">
          Name is required
      </div>
      <div class="form-text text-danger" *ngIf="name.errors?.['minlength'] || name.errors?.['maxlength'] && (name.dirty || name.touched)">
        Name should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="depid">Department Id:</label>
        <select class="form-control" id="depid" formControlName="depid" required>
          <option value="">Select Department</option>
          <option *ngFor="let department of departments" [value]="department.depid">{{ department.name }}</option>
        </select>
        <div class="form-text text-danger" *ngIf="depid.errors?.required && (depid.dirty || depid.touched)">
          Department is required
        </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="cnic">Cnic:</label>
        <input type="text" class="form-control" id="cnic" placeholder="Enter your cnic" formControlName="cnic"  required>
        <div class="form-text text-danger"  *ngIf="cnic.errors?.['required'] && (cnic.dirty || cnic.touched)">
          Cnic is required
      </div>
      <div class="form-text text-danger" *ngIf="cnic.errors?.['minlength'] || cnic.errors?.['maxlength'] && (cnic.dirty || cnic.touched)">
        Cnic should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="age">Age:</label>
        <input type="number" class="form-control" id="age" placeholder="Enter your Age" formControlName="age"  required>
        <div class="form-text text-danger"  *ngIf="age.errors?.['required'] && (age.dirty || age.touched)">
          Age is required
      </div>
      <div class="form-text text-danger" *ngIf="age.errors?.['minlength'] || age.errors?.['maxlength'] && (age.dirty || age.touched)">
        AGE should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="salary">Salary:</label>
        <input type="number" class="form-control" id="salary" placeholder="Enter your Salary" formControlName="salary"  required>
        <div class="form-text text-danger"  *ngIf="salary.errors?.['required'] && (salary.dirty || salary.touched)">
          Salary is required
      </div>
      <div class="form-text text-danger" *ngIf="salary.errors?.['minlength'] || salary.errors?.['maxlength'] && (salary.dirty || salary.touched)">
      Salary should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="text-center">
        <button type="submit" [disabled]="addEmployee.invalid" class="btn btn-primary">Submit</button>
      </div>
  </form>
    </div>
</div>
