import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Service } from 'src/app/services/service';
import { ActivatedRoute, Router } from '@angular/router';
import { editEmployee } from 'src/app/models/employeeModel';
import { editDepartment } from 'src/app/models/departmentModel';
@Component({
  selector: 'app-edit',
  templateUrl: './depedit.component.html',
  styleUrls: ['./depedit.component.css'],
})
export class DepeditComponent implements OnInit {
  constructor(
    private service: Service,
    private router: Router,
    private Router: ActivatedRoute
  ) {}
  employees: any[] = [];
  ngOnInit(): void {
    this.service.getAllEmployee().subscribe((data: any[]) => {
      this.employees = data;
    });
    const id = this.Router.snapshot.queryParams.id;
    this.service.getDepartmentbyid(id).subscribe((data: any) => {
      this.editDepartment.patchValue({
        name: data.name,
        headname:data.headname,
        depid: data.depId,
        location: data.location,
      });
    });
  }
  editDepartment = new FormGroup({

    name: new FormControl('', [
      Validators.required,
      Validators.pattern('[a-zA-Z ]*'),
      Validators.minLength(1),
      Validators.maxLength(14),
    ]),
    depid: new FormControl('', [Validators.required]),
    headname: new FormControl('', [Validators.required]),
    location: new FormControl('', [
      Validators.required,
      Validators.pattern('[a-zA-Z ]*'),
      Validators.minLength(2),
      Validators.maxLength(25),
    ]),
  });
  Submit() {
    const department: editDepartment = {
      depId: parseInt(this.editDepartment.get('depid')?.value || ''),
      name: this.editDepartment.get('name')?.value as string,
      headName: this.editDepartment.get('headname')?.value as string,
      location: this.editDepartment.get('location')?.value as string,
    };
    this.service.updateDepartment(department).subscribe(
      (resp) => {
        this.router.navigate(['pages/dephome']);
      },
      (error) => {
        alert('Unsuccessful !');
      }
    );
  }


  get depid(): FormControl {
    return this.editDepartment.get('depid') as FormControl;
  }
  get name(): FormControl {
    return this.editDepartment.get('name') as FormControl;
  }
  get headname(): FormControl {
    return this.editDepartment.get('headname') as FormControl;
  }
  get location(): FormControl {
    return this.editDepartment.get('location') as FormControl;
  }
}
<div class="container ">
    <div class="mx-auto border p-3 bg-light shadow-lg" style="width: 400px; ">
  <h2><b>Edit</b></h2>
  <form [formGroup]="editDepartment" (ngSubmit)="Submit()">
      <div class="mb-3">
        <label class="form-label" for="name">Department Name:</label>
        <input type="text" class="form-control" id="name" placeholder="Enter your Name" formControlName="name"  required>
        <div class="form-text text-danger"  *ngIf="(name.touched || name.dirty) && (name.errors?.required || name.errors?.pattern)">
            Department Name is required and should only contain alphabets.
      </div>
      <div class="form-text text-danger" *ngIf="name.errors?.['minlength'] || name.errors?.['maxlength'] && (name.dirty || name.touched       )">
        Department  Name should be 2 and as long as 25 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="headname">Head Name:</label>
        <select class="form-control" id="headname" formControlName="headname" required>
          <option value="">Select Head Name</option>
          <option *ngFor="let employee of employees" [value]="employee.name">{{ employee.name }}</option>
        </select>
        <div class="form-text text-danger" *ngIf="headname.errors?.required && (headname.dirty || headname.touched)">
          Head Name is required
        </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="location">Location:</label>
        <input type="text" class="form-control" id="location" placeholder="Enter your Location" formControlName="location"  required>
        <div class="form-text text-danger"   *ngIf="(location.touched || location.dirty) && (location.errors?.required || location.errors?.pattern)">
          Cnic number is required
      </div>
      <div class="form-text text-danger" *ngIf="location.errors?.['minlength'] || location.errors?.['maxlength'] && (location.dirty || location.touched)">
        Location should be 2 and as long as 25 characters long
    </div>
      </div>
      <br>
      <div class="btn-group d-flex justify-content-center" >
        <button type="submit" [disabled]="editDepartment.invalid" class="btn btn-success">Submit</button>
        &nbsp; &nbsp; <button class="btn btn-primary" [routerLink]="['/pages/dephome']">Cancel</button>
      </div>
  </form>
    </div>
</div>
