import { Component, OnInit } from '@angular/core';
import {
  FormGroup,
  FormBuilder,
  FormArray,
  FormControl,
  Validators,
} from '@angular/forms';
import { AuthService } from  'src/app/services/auth.service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent implements OnInit {

  constructor(private addAuth: AuthService, private router: Router) {}

  ngOnInit(): void { }
  addForm = new FormGroup({
    id: new FormControl('', [Validators.required]),
    name: new FormControl('', [
      Validators.required,
      Validators.minLength(3),
      Validators.maxLength(14),
    ]),
    depid: new FormControl('', [Validators.required]),
    cnic: new FormControl('', [
      Validators.required,
      Validators.minLength(13),
      Validators.maxLength(13),
    ]),
    age: new FormControl('', [
      Validators.required,
      Validators.minLength(2),
      Validators.maxLength(2),
    ]),
    salary: new FormControl('', [
      Validators.required,
      Validators.minLength(5),
      Validators.maxLength(6),
    ]),
  });
  isUserValid: boolean = false;
  Submitted() {
    this.addAuth
      .addEmployee([
        this.addForm.value.id as string,
        this.addForm.value.name as string,
        this.addForm.value.depid as string,
        this.addForm.value.cnic as string,
        this.addForm.value.age as string,
        this.addForm.value.salary as string,
      ])
      .subscribe((resp) => {
        if (resp == 'Failure') {
          this.isUserValid = false;
          alert('Unsuceesful');
        } else {

          this.router.navigate(['pages/home']);


        }
      });
  }


  get id(): FormControl {
    return this.addForm.get('id') as FormControl;
  }
  get name(): FormControl {
    return this.addForm.get('name') as FormControl;
  }
  get depid(): FormControl {
    return this.addForm.get('depid') as FormControl;
  }
  get cnic(): FormControl {
    return this.addForm.get('cnic') as FormControl;
  }
  get age(): FormControl {
    return this.addForm.get('age') as FormControl;
  }
  get salary(): FormControl {
    return this.addForm.get('salary') as FormControl;
  }
}

<div class="container pt-5 ">
    <div class="mx-auto border p-4 bg-light " style="width: 400px; ">
  <h2 >Create an account</h2>
  <form [formGroup]="addForm" (ngSubmit)="Submitted()">
    <div class="mb-3">
        <label class="form-label" for="id">Id:</label>
        <input type="text" class="form-control" id="id" placeholder="Enter your id" formControlName="id" required>
        <div class="form-text text-danger" *ngIf="id.errors?.['required'] && (id.dirty || id.touched)">
            Id is required
        </div>
        <div class="form-text text-danger"*ngIf="id.errors?.['email'] && (id.dirty || id.touched)">
          Id is invalid
      </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="name">Name:</label>
        <input type="text" class="form-control" id="name" placeholder="Enter your Name" formControlName="name"  required>
        <div class="form-text text-danger"  *ngIf="name.errors?.['required'] && (name.dirty || name.touched)">
          Name is required
      </div>
      <div class="form-text text-danger" *ngIf="name.errors?.['minlength'] || name.errors?.['maxlength'] && (name.dirty || name.touched)">
        Name should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="depid">DeId:</label>
        <input type="number" class="form-control" id="depid" placeholder="Enter your depid" formControlName="depid"  required>
        <div class="form-text text-danger"  *ngIf="depid.errors?.['required'] && (depid.dirty || depid.touched)">
          dep  is required
      </div>
      <div class="form-text text-danger" *ngIf="depid.errors?.['minlength'] || depid.errors?.['maxlength'] && (depid.dirty || depid.touched)">
      dep id required
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="cnic">Cnic:</label>
        <input type="text" class="form-control" id="cnic" placeholder="Enter your cnic" formControlName="cnic"  required>
        <div class="form-text text-danger"  *ngIf="cnic.errors?.['required'] && (cnic.dirty || cnic.touched)">
          Cnic is required
      </div>
      <div class="form-text text-danger" *ngIf="cnic.errors?.['minlength'] || cnic.errors?.['maxlength'] && (cnic.dirty || cnic.touched)">
        Cnic should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="age">age:</label>
        <input type="number" class="form-control" id="age" placeholder="Enter your age" formControlName="age"  required>
        <div class="form-text text-danger"  *ngIf="age.errors?.['required'] && (age.dirty || age.touched)">
          Age is required
      </div>
      <div class="form-text text-danger" *ngIf="age.errors?.['minlength'] || age.errors?.['maxlength'] && (age.dirty || age.touched)">
        AGE should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="salary">Salary:</label>
        <input type="number" class="form-control" id="salary" placeholder="Enter your Salary" formControlName="salary"  required>
        <div class="form-text text-danger"  *ngIf="salary.errors?.['required'] && (salary.dirty || salary.touched)">
          Salary is required
      </div>
      <div class="form-text text-danger" *ngIf="salary.errors?.['minlength'] || salary.errors?.['maxlength'] && (age.dirty || age.touched)">
        AGE should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class="text-center">
        <button type="submit" [disabled]="addForm.invalid" class="btn btn-primary">Signup</button>
      </div>
      <hr>

  </form>
    </div>
</div>
