import { AfterViewInit, Component, OnInit, ViewChild } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { DataTableDirective } from 'angular-datatables';
import { Router } from '@angular/router';
import { BsModalService, BsModalRef } from 'ngx-bootstrap/modal';
import { RendererFactory2 } from '@angular/core';
import { Subject } from 'rxjs';
import { id } from '@swimlane/ngx-datatable';

@Component({
  selector: 'app-student',
  templateUrl: './st.component.html',
  styleUrls: ['./student.component.css']
})
export class StudentComponent implements OnInit{

  constructor(private http: HttpClient, private _router: Router) { }

  dtOptions: DataTables.Settings = {};
 
  columnsDataObj = [
    { title: "Std_Id", data: "std_Id", visible: false },
    { title: "Name", data: "name" },
    { title: "Address",  data: "address" , sorting : false },
    { title: "Roll_No", data: "roll_No", sorting: false },
    { title: "Standrad", data: "standard", sorting: false },
    { title: "Marks", data: "marks", sorting: false },
    { title: "Age", data: "age", sorting: false },
    { title: "Phone", data: "phone", sorting: false },
    { title: "Dep_Id", data: "dep_Id", visible: false },
 
    {
     
      "data": id,
      "render": function (data: any) {
        return '<a class=" btn btn-warning" click="addRecord()"> Edit </a>'      + " " +
          '<button type="button" class="btn btn-danger" (click)="addRecord()">Delete</button>';
      }
    }
  ];
 

  ngOnInit(): void {
    this.fetchData();
    };


  fetchData() {
    this.dtOptions = {
      "processing": true,
      "serverSide": true,
      "paging": true,
      "searching": true,
      "ordering": true,
      "ajax":
        (dataTablesParameters: any, callback) => {
          this.http.post('https://localhost:7280/api/Student/GetAll', dataTablesParameters).subscribe((Response: any) => {
            callback({
              recordsTotal: Response.recordsTotal,
              recordsFiltered: Response.recordsFiltered,
              data: Response.data,
            });
          });
        },
      "columns": this.columnsDataObj
    };
  }
