import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Service } from 'src/app/services/service';
import { ActivatedRoute, Router } from '@angular/router';
import { createEmployee } from 'src/app/models/employeeModel';
@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css'],
})
export class AddComponent implements OnInit {
  constructor(
    private service: Service,
    private router: Router,
  ) {}
  departments: any[] = [];
  ngOnInit(): void {
    this.service.getAllDepartments().subscribe((data: any[]) => {
      this.departments = data;
    });
  }
  addEmployee = new FormGroup({
    name: new FormControl('', [
      Validators.required,
      Validators.pattern('[a-zA-Z ]*'),
      Validators.minLength(1),
      Validators.maxLength(14),
    ]),
    depid: new FormControl('', [Validators.required]),
    cnic: new FormControl('', [
      Validators.required,
      Validators.pattern('[0-9]*'),
      Validators.minLength(13),
      Validators.maxLength(13),
    ]),
    age: new FormControl('', [
      Validators.required,
      Validators.pattern('[0-9]*'),
      Validators.min(18),
      Validators.max(59),
    ]),
    salary: new FormControl('', [
      Validators.required,
      Validators.pattern('[0-9]*'),
      Validators.min(10000),
      Validators.max(100000),
    ]),
  });
  Submit(): void {
    const employee: createEmployee = {
      name: this.addEmployee.get('name')?.value as string,
      age: parseInt(this.addEmployee.get('age')?.value || ''),
      salary: parseInt(this.addEmployee.get('salary')?.value || ''),
      cnic: this.addEmployee.get('cnic')?.value as string,
      depId: parseInt(this.addEmployee.get('depid')?.value || ''),
    };
    this.service.addEmployee(employee).subscribe(
      (resp) => {
        this.router.navigate(['pages/home']);
      },
      (error) => {
        alert('Unsuccessful !');
      }
    );
  }
  get name(): FormControl {
    return this.addEmployee.get('name') as FormControl;
  }
  get depid(): FormControl {
    return this.addEmployee.get('depid') as FormControl;
  }
  get cnic(): FormControl {
    return this.addEmployee.get('cnic') as FormControl;
  }
  get age(): FormControl {
    return this.addEmployee.get('age') as FormControl;
  }
  get salary(): FormControl {
    return this.addEmployee.get('salary') as FormControl;
  }
}
<div class="container pb-1 ">
    <div class="mx-auto border p-4 bg-light " style="width: 400px; ">
  <h2 class="text-center" >Add Employee</h2>
  <form [formGroup]="addEmployee" (ngSubmit)="Submit()">
      <div class="mb-3">
        <label class="form-label" for="name">Name:</label>
        <input type="text" class="form-control" id="name" placeholder="Enter your Name" formControlName="name"  required>
        <div class="form-text text-danger"  *ngIf="(name.touched || name.dirty) && (name.errors?.required || name.errors?.pattern)">
          Name is required and should only contain alphabets.
      </div>
      <div class="form-text text-danger" *ngIf="name.errors?.['minlength'] || name.errors?.['maxlength'] && (name.dirty || name.touched       )">
        Name should be at  1 and as long as 14 characters long
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="depid">Department:</label>
        <select class="form-control" id="depid" formControlName="depid" required>
          <option value="">Select Department</option>
          <option *ngFor="let department of departments" [value]="department.depid">{{ department.name }}</option>
        </select>
        <div class="form-text text-danger" *ngIf="depid.errors?.required && (depid.dirty || depid.touched)">
          Department is required
        </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="cnic">Cnic:</label>
        <input type="text" class="form-control" id="cnic" placeholder="Enter your cnic number" formControlName="cnic"  required>
        <div class="form-text text-danger"   *ngIf="(cnic.touched || cnic.dirty) && (cnic.errors?.required || cnic.errors?.pattern)">
          Cnic number is required
      </div>
      <div class="form-text text-danger" *ngIf="cnic.errors?.['minlength'] || cnic.errors?.['maxlength'] && (cnic.dirty || cnic.touched)">
        Cnic should be 13 numbers long (without dashes)
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="age">Age:</label>
        <input  class="form-control" id="age" placeholder="Enter your Age" formControlName="age"  required>
        <div class="form-text text-danger"   *ngIf="(age.touched || age.dirty) && (age.errors?.required || age.errors?.pattern)">
          Age is required
      </div>
      <div class="form-text text-danger" *ngIf="age.errors?.['min'] || age.errors?.['max'] && (age.dirty || age.touched)">
      Age should be atleast 18 to 59
    </div>
      </div>
      <div class="mb-3">
        <label class="form-label" for="salary">Salary:</label>
        <input  class="form-control" id="salary" placeholder="Enter your Salary" formControlName="salary"  required>
        <div class="form-text text-danger"   *ngIf="(salary.touched || salary.dirty) && (salary.errors?.required || salary.errors?.pattern)">
          Salary is required
      </div>
      <div class="form-text text-danger" *ngIf="salary.errors?.['min'] || salary.errors?.['max'] && (salary.dirty || salary.touched)">
      Salary should be 10000 to 100000
    </div>
      </div>
      <div class="text-center">
        <button type="submit" [disabled]="addEmployee.invalid" class="btn btn-primary">Submit</button>
      </div>
      <button class="btn btn-primary"  [routerLink]="['/pages/add']">Add Employee</button>
  </form>
    </div>
</div>
