import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { HttpClient } from '@angular/common/http';
import {  Router } from '@angular/router';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  dtOptions: DataTables = {};

  constructor(private authService: AuthService, private http: HttpClient,private router: Router) {}

  ngOnInit(): void {
    this.dtOptions = {
      ajax: (dataTablesParameters: any, callback: any) => {
        this.authService.getData().subscribe((response: any) => {
          callback({
            draw: response.draw,
            recordsTotal: response.recordsTotal,
            recordsFiltered: response.recordsFiltered,
            data: response.data,
          });
        });
      },
      columns: [
        { title: 'ID', data: 'id', visible: false },
        { title: 'Name', data: 'name' },
        { title: 'DepId', data: 'depId', visible: false },
        { title: 'Cnic', data: 'cnic' },
        { title: 'Age', data: 'age' },
        { title: 'Salary', data: 'salary' },
        {
          title: 'Actions',
          data: null,
          render: (data: any, type: any, full: any, meta: any) => {
            return '<a class="btn btn-info"href="/pages/edit/' + data.id + '">Edit</a>  '+
            '<a class="btn btn-danger delete-button" id="deleteButton" data-toggle="modal" data-target="#deleteConfirmationModal" data-id="' + data.id + '">Delete</a>';
          },
        },
      ],

      paging: true,
      //pageLength: 10,
      //searching: true,
      ordering: true,
      filter:true,
      serverSide:true,
      /*columnDefs: [
        {
          targets: [1],
          orderable: true,
          searchable: true,
        },
        {
          targets: '_all',
          orderable: false,
          searchable: false,
        },
      ],*/
     
    };
    
  }
  
 }
