import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Service } from '../services/service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  constructor(private service: Service, private router: Router) {}
  ngOnInit(): void {}
  loginForm = new FormGroup({
    inputEmail: new FormControl('', [Validators.required, Validators.email]),
    inputPassword: new FormControl('', [
      Validators.required,
      Validators.minLength(8),
      Validators.maxLength(14),
    ]),
  });
  Submitted() {
    this.service
      .loginUser([
        this.loginForm.value.inputEmail as string,
        this.loginForm.value.inputPassword as string,
      ])
      .subscribe(
        (resp) => {
          const respObj = JSON.parse(resp);
          this.router.navigate(['pages/home']);
          localStorage.setItem('token', respObj.token);
          alert('Successfully Login');
        },
        (error) => {
          alert('Username or Password is incorrect !');
        }
      );
  }
  get Email(): FormControl {
    return this.loginForm.get('inputEmail') as FormControl;
  }
  get PWD(): FormControl {
    return this.loginForm.get('inputPassword') as FormControl;
  }
}
<div class="container pt-5 " >
  <div class="mx-auto border p-4 bg-light " style="width: 400px; ">
    <form [formGroup]="loginForm" (ngSubmit)="Submitted()">
      <div class="mb-3">
        <input type="email" class="form-control" id="inputEmail" placeholder="Email address" formControlName="inputEmail" required>
        <div class="form-text text-danger" *ngIf="Email.errors?.['required'] && (Email.dirty || Email.touched)">
            Email is required
        </div>
        <div class="form-text text-danger" *ngIf="Email.errors?.['email'] && (Email.dirty || Email.touched)">
          Email is invalid
      </div>
      </div>
      <div class="mb-3">
        <input type="password" class="form-control" id="inputPassword" placeholder="Password" formControlName="inputPassword"  required>
        <div class="form-text text-danger" *ngIf="PWD.errors?.['required'] && (PWD.dirty || PWD.touched)">
          Password is required
      </div>
      <div class="form-text text-danger" *ngIf="PWD.errors?.['minlength'] || PWD.errors?.['maxlength'] && (Email.dirty || Email.touched)">
        Password should be at least 6 and as long as 14 characters long
    </div>
      </div>
      <div class=" d-grid gap-2">
        <button type="submit" [disabled]="loginForm.invalid" class="btn btn-primary  "><b>Log in</b></button>
      </div>
      <hr>
      <p class="text-center">Don't have an account? <a [routerLink]="['signup']"
        class="fw-bold text-body"><u>Signup</u></a></p>
    </form>
  </div>
</div>
