import { ErrorHandler, Injectable } from '@angular/core';
import { LoggingService } from 'src/app/services/logging.service';

@Injectable()
export class GlobalErrorHandler implements ErrorHandler {
  constructor(private loggingService: LoggingService) {}

  handleError(error: any): void {
    // Log the error using the LoggingService
    const message = 'An error occurred: ' + error.message;
    const level = 'ERROR';
    this.loggingService.log(message, level);

    // Optionally, you can also display a user-friendly error message or perform other error handling tasks

    // Re-throw the error to ensure it's still caught by Angular's default error handling mechanism
    throw error;
  }
}
