import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  constructor(private http: HttpClient, private _router: Router) {}
  dtOptions: DataTables.Settings = {};
  ngOnInit(): void {
    this.fetchData();
  }
  fetchData() {
    this.dtOptions = {
      processing: true,
      serverSide: true,
      paging: true,
      searching: true,
      ordering: true,
      ajax: (dataTablesParameters: any, callback) => {
        this.http
          .post(
            'https://localhost:7149/api/Employee/all-Employees',
            dataTablesParameters
          )
          .subscribe((Response: any) => {
            callback({
              recordsTotal: Response.recordsTotal,
              recordsFiltered: Response.recordsFiltered,
              data: Response.data,
            });
          });
      },
      columns: [
        { title: 'Id', data: 'id', visible: false },
        { title: 'Name', data: 'name' },
        { title: 'DepId', data: 'depId', visible: false },
        { title: 'Cnic', data: 'cnic' },
        { title: 'Age', data: 'age' },
        { title: 'Salary', data: 'salary' },
        {
          data: null,
          render: function (data: any) {
            return (
              '<a class="btn btn-info"href="/pages/edit/' +
              data.id +
              '">Edit</a>  ' +
              '<a class="btn btn-danger delete-button" id="deleteButton" data-toggle="modal" data-target="#deleteConfirmationModal" data-id="' +
              data.id +
              '">Delete</a>'
            );
          },
        },
      ],
      columnDefs: [
        {
          targets: [1],
          orderable: true,
          searchable: true,
        },
        {
          targets: '_all',
          orderable: false,
          searchable: false,
        },
      ],
    };
  }
}
