import { AfterViewInit, Component, OnInit, ViewChild } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { DataTablesModule } from 'angular-datatables';
import { Router } from '@angular/router';
//import { BsModalService, BsModalRef } from 'ngx-bootstrap/modal';
import { RendererFactory2 } from '@angular/core';
import { Subject } from 'rxjs';
import { id } from '@swimlane/ngx-datatable';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
 // styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit{

  constructor(private http: HttpClient, private _router: Router) { }

  dtOptions: DataTables.Settings = {};
 
  columnsDataObj = [
    { title: "Id", data: "id", visible: false},
    { title: "Name", data: "name" },
    { title: "DepId",  data: "depId" , sorting : false, visible: false },
    { title: "Cnic", data: "cnic", sorting: false },
    { title: "Age", data: "age", sorting: false },
    { title: "Salary", data: "salary", sorting: false },
 
    {
     
      "data": null,
      "render": function (data: any) {
        return '<a class="btn btn-info"href="/pages/edit/' + data.id + '">Edit</a>  '+
        '<a class="btn btn-danger delete-button" id="deleteButton" data-toggle="modal" data-target="#deleteConfirmationModal" data-id="' + data.id + '">Delete</a>';
      }
    }
  ];
 

  ngOnInit(): void {
    this.fetchData();
    };


  fetchData() {
    this.dtOptions = {
      "processing": true,
      "serverSide": true,
      "paging": true,
      "searching": true,
      "ordering": true,
      "ajax":
        (dataTablesParameters: any, callback) => {
          this.http.post('https://localhost:7149/api/Employee/all-Employees', dataTablesParameters).subscribe((Response: any) => {
            callback({
              recordsTotal: Response.recordsTotal,
              recordsFiltered: Response.recordsFiltered,
              data: Response.data,
            });
          });
        },
      "columns":[
        { "title": "Id", "data": "id", "visible": false},
        { title: "Name", data: "name" },
        { title: "DepId",  data: "depId" , sorting : false, visible: false },
        { title: "Cnic", data: "cnic", sorting: false },
        { title: "Age", data: "age", sorting: false },
        { title: "Salary", data: "salary", sorting: false },
     
        {
         
          "data": null,
          "render": function (data: any) {
            return '<a class="btn btn-info"href="/pages/edit/' + data.id + '">Edit</a>  '+
            '<a class="btn btn-danger delete-button" id="deleteButton" data-toggle="modal" data-target="#deleteConfirmationModal" data-id="' + data.id + '">Delete</a>';
          }
        }
      ],
      "columnDefs": [
        {
          targets: [1],
          orderable: true,
          searchable: true,
        },
        {
          targets: '_all',
          orderable: false,
          searchable: false,
        },
      ],
    };
  }
}
