import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent implements OnInit {
  addForm: FormGroup;
  isUserValid: boolean = false;

  constructor(
    private formBuilder: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.addForm = this.formBuilder.group({
      id: ['', Validators.required],
      name: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(14)]],
      depid: ['', Validators.required],
      cnic: ['', [Validators.required, Validators.minLength(13), Validators.maxLength(13)]],
      age: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(2)]],
      salary: ['', [Validators.required, Validators.minLength(5), Validators.maxLength(6)]],
    });
  }

  Submitted() {
    if (this.addForm.invalid) {
      return;
    }

    this.authService
      .addEmployee([
        this.addForm.value.id,
        this.addForm.value.name,
        this.addForm.value.age,
        this.addForm.value.salary,
        this.addForm.value.cnic,
        this.addForm.value.depid,
      ])
      .subscribe((resp) => {
        if (resp === 'Failure') {
          this.isUserValid = false;
          alert('Unsuccessful');
        } else {
          this.router.navigate(['pages/home']);
        }
      });
  }

  get formControls() {
    return this.addForm.controls;
  }
}

<div class="container pt-5 ">
    <div class="mx-auto border p-4 bg-light " style="width: 400px; ">
  <h2 >Create an account</h2>
  <form [formGroup]="addForm" (ngSubmit)="Submitted()">
    <div class="mb-3"hidden>
        <label class="form-label" for="id">Id:</label>
        <input type="text" class="form-control" id="id" placeholder="Enter your id" formControlName="id" >
