import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  constructor(private http: HttpClient, private _router: Router,private authService:A) {}
  dtOptions: DataTables.Settings = {};
  ngOnInit(): void {
    this.fetchData();
  }
  fetchData() {
    this.dtOptions = {
      processing: true,
      serverSide: true,
      paging: true,
      searching: true,
      ordering: true,
      ajax: (dataTablesParameters: any, callback) => {
        this.http
          .post(
            'https://localhost:7149/api/Employee/all-Employees',
            dataTablesParameters
          )
          .subscribe((Response: any) => {
            callback({
              recordsTotal: Response.recordsTotal,
              recordsFiltered: Response.recordsFiltered,
              data: Response.data,
            });
          });
      },
      columns: [
        { title: 'Id', data: 'id', visible: false },
        { title: 'Name', data: 'name' },
        { title: 'DepId', data: 'depId', visible: false },
        { title: 'Cnic', data: 'cnic' },
        { title: 'Age', data: 'age' },
        { title: 'Salary', data: 'salary' },
        { title:'Actions',
          data: null,
          render:function () {
            return '<button class = "btn btn-info edit">Edit</button>       <button class = "btn btn-danger delete">Delete</button>'
          },
        },
      ],
      columnDefs: [
        {
          targets: [1],
          orderable: true,
          searchable: true,
        },
        {
          targets: '_all',
          orderable: false,
          searchable: false,
        },
      ],
      rowCallback: (row: Node, data: any, index: number) => {
        const self = this;
        $('.edit', row).off('click');
        $('.edit', row).on('click', () => {
          
          this._router.navigate(['pages/edit'], { queryParams: { id: data.id } });
        });
        $('.delete', row).off('click');
        $('.delete', row).on('click', () => {
          const id = data.id;
          alert('Are you sure you want to delete this Employee with ID: ' + id);
    
          this.http.delete
        });
        return row;
      }
    };
  };
  
}
