import { Component, OnInit } from '@angular/core';
import {
  FormGroup,
  FormBuilder,
  FormArray,
  FormControl,
  Validators,
} from '@angular/forms';
import { AuthService } from 'src/app/services/auth.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Employee } from 'src/app/models/employee';
@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css'],
})
export class EditComponent implements OnInit {
  constructor(
    private signinAuth: AuthService,
    private router: Router,
    private Router: ActivatedRoute
  ) {}
  ngOnInit(): 

  void {
    this.s
    const id = this.Router.snapshot.params.id;
    this.signinAuth.getEmployeebyid(id).subscribe((data: any) => {
      this.addForm.patchValue({
        id: data.id,
        name: data.name,
        depid: data.depId,
        cnic: data.cnic,
        age: data.age,
        salary: data.salary,
      });
    });
  }
  addForm = new FormGroup({
    id: new FormControl('', [Validators.required]),
    name: new FormControl('', [
      Validators.required,
      Validators.minLength(3),
      Validators.maxLength(14),
    ]),
    depid: new FormControl('', [Validators.required]),
    cnic: new FormControl('', [
      Validators.required,
      Validators.minLength(13),
      Validators.maxLength(13),
    ]),
    age: new FormControl('', [
      Validators.required,
      Validators.minLength(2),
      Validators.maxLength(2),
    ]),
    salary: new FormControl('', [
      Validators.required,
      Validators.minLength(5),
      Validators.maxLength(6),
    ]),
  });
  isUserValid: boolean = false;
  Submitted() {
    const employee: Employee = {
      id: parseInt(this.addForm.get('id')?.value || ''),
      name: this.addForm.get('name')?.value,
      age: parseInt(this.addForm.get('age')?.value || ''),
      salary: parseInt(this.addForm.get('salary')?.value || ''),
      cnic: this.addForm.get('cnic')?.value,
      depId: parseInt(this.addForm.get('depid')?.value || ''),
    };

    this.signinAuth.updateEmployee(employee).subscribe((resp: any[]) => {
      if (resp.length === 0) {
        this.isUserValid = false;
        alert('Unsuccessful');
      } else {
        this.router.navigate(['pages/home']);
      }
    });
  }
  get id(): FormControl {
    return this.addForm.get('id') as FormControl;
  }
  get name(): FormControl {
    return this.addForm.get('name') as FormControl;
  }
  get depid(): FormControl {
    return this.addForm.get('depid') as FormControl;
  }
  get cnic(): FormControl {
    return this.addForm.get('cnic') as FormControl;
  }
  get age(): FormControl {
    return this.addForm.get('age') as FormControl;
  }
  get salary(): FormControl {
    return this.addForm.get('salary') as FormControl;
  }
}
