import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  constructor(private http: HttpClient, private _router: Router) {}
  dtOptions: DataTables.Settings = {};
  ngOnInit(): void {
    this.fetchData();
  }
  fetchData() {
    this.dtOptions = {
      processing: true,
      serverSide: true,
      paging: true,
      searching: true,
      ordering: true,
      ajax: (dataTablesParameters: any, callback) => {
        this.http
          .post(
            'https://localhost:7149/api/Employee/all-Employees',
            dataTablesParameters
          )
          .subscribe((Response: any) => {
            callback({
              recordsTotal: Response.recordsTotal,
              recordsFiltered: Response.recordsFiltered,
              data: Response.data,
            });
          });
      },
      columns: [
        { title: 'Id', data: 'id', visible: false },
        { title: 'Name', data: 'name' },
        { title: 'DepId', data: 'depId', visible: false },
        { title: 'Cnic', data: 'cnic' },
        { title: 'Age', data: 'age' },
        { title: 'Salary', data: 'salary' },
        {
          data: null,
          render: function (data: any) {
            return (
              '<a class="btn btn-info"href="/pages/edit/' +
              data.id +
              '">Edit</a>  ' +
              '<a class="btn btn-danger " href="/pages/delete/' +
              data.id +
              '">Delete</a>'
            );
          },
        },
      ],
      columnDefs: [
        {
          targets: [1],
          orderable: true,
          searchable: true,
        },
        {
          targets: '_all',
          orderable: false,
          searchable: false,
        },
      ],rowCallback: (row: EL, data: any[] | Object, index: number) => {
        const self = this;
        const editButton = row.querySelector('.edit-button') as HTMLElement;
        const deleteButton = row.querySelector('.delete-button');
    
        editButton.addEventListener('click', () => {
          const id = editButton.getAttribute('data-id');
          // Handle edit logic here, e.g., navigate to edit route
          window.location.href = '/pages/edit/' + id;
        });
    
        deleteButton.addEventListener('click', () => {
          const id = deleteButton.getAttribute('data-id');
          // Handle delete logic here, e.g., open a modal or perform an API request
          // and update the table afterward
          // ...
        });
    
        return row;
      },
    };
    };
  }
}
